$(OnReady);
function OnReady()
{
    var original = $("#panelbar").clone(true);
    original.find(".k-state-active").removeClass("k-state-active");
    $(".configuration input").change(function (e)
    {
        var panelBar = $("#panelbar");
        var clone = original.clone(true);
        panelBar.data("kendoPanelBar").collapse($("#panelbar .k-link"));
        panelBar.replaceWith(clone);
        initPanelBar();
    });
    initPanelBar();
};
function getEffects()
{
    var expand = $("#expand")[0].checked;
    var opacity = $("#opacity")[0].checked;
    var s = "";
    if (expand)
        s += "expand:vertical ";
    if (opacity)
        s += "fadeIn";
    if (s == "")
        return false;
    return s;
};
function initPanelBar()
{
    $("#panelbar").kendoPanelBar(
    {
        animation:
        {
            expand: {effects: getEffects()}
        }
    });
};
